/* WEATHER OBSERVATION STATION 2 */
/*
Query the following two values from the STATION table:
1. The sum of all values in LAT_N rounded to a scale of 2 decimal places.
2. The sum of all values in LONG_W rounded to a scale of 2 decimal places.

select '(' + convert(varchar(10),ID) + ",'" + City + "','" + State + "'," + Convert(varchar(20),Lat_N) + "," + + Convert(varchar(20),LONG_W) + "),"  from Station Order by 1 
*/

declare @Station Table (ID int, City Varchar(22), State Varchar(2), Lat_N float, Long_W float)

--select * from @Station order by 1

select cast(round(Sum(Lat_N),2) as numeric(36,2)) as Lat, cast(round(Sum(Long_W),2) as numeric(36,2)) as Lon from @Station 

